import React from "react";
import "./App.css";
import { Button } from "react-bootstrap";
import { ButtonGroup } from "react-bootstrap";

function App() {
  return (
    <div className="App">
      <ButtonGroup className="m-2">
        <Button sm={4} value={1} variant="outline-primary" size="lg">
          <span className="emoji" role="img" aria-label="increment">
            {"➕"}
          </span>
        </Button>
        <Button value={2} variant="outline-danger" size="lg">
          <span className="emoji" role="img" aria-label="decrement">

            ➖{" "}
          </span>
        </Button>
      </ButtonGroup>
    </div>
  );
}

export default App;
