import React, { useState } from "react";

function InputText(props) {
  const [value, setValue] = useState("");

  return (
    <form onSubmit={e => {
        e.preventDefault();
        props.handleSubmit(value);
        setValue("")
        }}>
      Oa<input
        type="text"
        placeholder="Your server..."
        value={value}
        onChange={e => setValue(e.target.value)}
      />
    </form>
  );
}

export default InputText;
