import React, { useState } from "react";
import "./App.css";
import { Button, Badge } from "react-bootstrap";
import { ButtonGroup } from "react-bootstrap";

function App() {
  const [count, setCount] = useState(0);
  return (
    <div className="App">
      <ButtonGroup className="m-2">
        <Button
          onClick={() => setCount(count + 1)}
          sm={4}
          value={1}
          variant="outline-primary"
          size="lg"
        >
          <span className="emoji" role="img" aria-label="increment">
            {"➕"}
          </span>
        </Button>
        <Button
          onClick={() => setCount(count - 1)}
          value={2}
          variant="outline-primary"
          size="lg"
        >
          <span className="emoji" role="img" aria-label="decrement">
            {"➖ "}
          </span>
        </Button>
        <Button
          onClick={() => setCount(0)}
          value={3}
          variant="outline-danger"
          size="lg"
        >
          Reset{" "}
          <span className="emoji" role="img" aria-label="increment">
            {"✖"}
          </span>
        </Button>
      </ButtonGroup>
      <br />
      <h3 className="mt-2">
        <code style={{color: '#000000'}}>Count = </code>
        <code pill variant="info">
          {count}
        </code>{" "}
      </h3>
    </div>
  );
}

export default App;
