import React, { useState } from "react";
import "./App.css";
import { Button, InputGroup } from "react-bootstrap";
import { ButtonGroup } from "react-bootstrap";
import { InputGroupPrepend, InputGroupText } from "react-bootstrap/InputGroup";
import { FormControl } from "react-bootstrap";

function App() {
  const [count, setCount] = useState(0);
  return (
    <div className="App">
      <ButtonGroup className="m-2">
        <Button
          onClick={() => setCount(count + 1)}
          sm={4}
          value={1}
          variant="outline-info"
          size="lg"
        >
          <span className="emoji" role="img" aria-label="increment">
            {"➕"}
          </span>
        </Button>
        <Button
          onClick={() => setCount(count - 1)}
          value={2}
          variant="outline-info"
          size="lg"
        >
          <span className="emoji" role="img" aria-label="decrement">
            {"➖ "}
          </span>
        </Button>
        <Button
          onClick={() => setCount(0)}
          value={3}
          variant="outline-danger"
          size="lg"
        >
          Reset{" "}
          <span className="emoji" role="img" aria-label="increment">
            {"✖"}
          </span>
        </Button>
      </ButtonGroup>
      <br />
      <InputGroup className="mt-3">
        <InputGroupPrepend>
          <InputGroupText id="Count"></InputGroupText>
        </InputGroupPrepend>
        <FormControl aria-label=""></FormControl>
      </InputGroup>
    </div>
  );
}

export default App;
