import React, { useState } from "react";
import "./App.css";
import { Button } from "react-bootstrap";
import { ButtonGroup } from "react-bootstrap";

function App({initialCount}) {
  const [count, setCount] = useState(initialCount);
  return (
    <div className="App">
      <ButtonGroup className="m-2">
        <Button onClick={() => setCount(prevCount => prev)} sm={4} value={1} variant="outline-primary" size="lg">
          <span className="emoji" role="img" aria-label="increment">
            {"➕"}
          </span>
        </Button>
        <Button onClick={() => setCount(prevCount => prev)} value={2} variant="outline-danger" size="lg">
          <span className="emoji" role="img" aria-label="decrement">
            {"➖ "}
          </span>
        </Button>
      </ButtonGroup>
        Count: {count}
    </div>
  );
}

export default App;
